import React, { useState, useEffect } from 'react';
import { Github, Linkedin, Mail, MapPin, ExternalLink, ChevronDown, Menu, X, Code, Briefcase, GraduationCap, User, Star } from 'lucide-react';
import './App.css';

// Navigation items
const navItems = [
  { id: 'about', label: 'About', icon: User },
  { id: 'experience', label: 'Experience', icon: Briefcase },
  { id: 'projects', label: 'Projects', icon: Code },
  { id: 'education', label: 'Education', icon: GraduationCap }
];

const Portfolio = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [activeSection, setActiveSection] = useState('about');

  // Personal Information
  const personalInfo = {
    name: "Mohammad Razak Abdul Rasheeth",
    title: "Full-Stack Developer",
    subtitle: "Software Engineer | Skilled in JavaScript, React, Node.js, Java, Express & MongoDB",
    description: "ðŸ‘‹ Hi, I'm Mohammad Razak, a full-stack developer passionate about building impactful, user-first web and mobile solutions. I specialize in MERN and React/Node.js with specialty in authentication, real-time systems, and deployment on zero-cost platforms.",
    location: "Coimbatore, Tamil Nadu, India",
    email: "your.email@example.com",
    phone: "+91 XXXXXXXXXX",
    linkedin: "https://linkedin.com/in/mohammadrazak-abdulrasheeth",
    github: "https://github.com/MohammadRazaak"
  };

  // Skills
  const skills = [
    {
      category: "Frontend",
      items: ["React", "Redux", "JavaScript", "Tailwind CSS", "Material UI", "React Router", "HTML5", "CSS3"]
    },
    {
      category: "Backend",
      items: ["Node.js", "Express", "Socket.io", "RESTful APIs", "JWT Authentication", "Java"]
    },
    {
      category: "Database",
      items: ["MongoDB", "PostgreSQL", "Prisma ORM", "Firebase"]
    },
    {
      category: "Tools & Deployment",
      items: ["Git", "Netlify", "Vercel", "Heroku", "Railway", "Google APIs", "Microsoft Azure"]
    }
  ];

  // Projects
  const projects = [
    {
      title: "Raz Insurance",
      description: "A sleek insurance platform enabling users to buy, manage, and verify policies and coverage online.",
      features: [
        "Full-stack with React, Redux, Node, Express & PostgreSQL via Prisma ORM",
        "Customer/admin roles with JWT-based auth & admin approval flows",
        "Responsive design and SEO-friendly PWA hosted on Netlify and Railway/Heroku"
      ],
      tech: ["React", "Redux", "Node.js", "Express", "PostgreSQL", "Prisma ORM"],
      liveUrl: "#",
      githubUrl: "#",
      status: "Production Ready"
    },
    {
      title: "RazChatz",
      description: "A real-time chat application built with React and Socket.io.",
      features: [
        "Instant messaging, seen/delivered statuses, emoji picker",
        "JWT authentication, Web Notifications, service-worker optimized for PWA",
        "Hosted as a PWA for installable mobile experience"
      ],
      tech: ["React", "Socket.io", "Node.js", "MongoDB", "Material UI"],
      liveUrl: "https://razchatz.netlify.app/",
      githubUrl: "#",
      status: "Live"
    },
    {
      title: "Placement Training System (PTS)",
      description: "An end-to-end placement prep suite for students and TPOs.",
      features: [
        "MERN stack with MongoDB and Express",
        "Integrated Google Sheets API for live job data management",
        "Firebase Storage for image hosting",
        "JWT authentication, role-based dashboards, quizzes, video lessons"
      ],
      tech: ["React", "Node.js", "MongoDB", "Google Sheets API", "Firebase"],
      liveUrl: "https://pts-by-razak.netlify.app",
      githubUrl: "#",
      status: "Live"
    }
  ];

  // Experience
  const experience = [
    {
      company: "ValueMomentum",
      position: "Software Engineer Intern",
      duration: "Feb 2025 - Present",
      location: "Coimbatore, Tamil Nadu, India",
      type: "On-site",
      description: "Working with Microsoft Azure and developing scalable software solutions.",
      achievements: [
        "Developing enterprise-level applications",
        "Working with cloud technologies",
        "Collaborating with cross-functional teams"
      ]
    }
  ];

  // Education
  const education = [
    {
      institution: "KG College of Arts and Science",
      degree: "Bachelor of Computer Application",
      duration: "Jul 2022 - May 2025",
      location: "Coimbatore, Tamil Nadu, India",
      specialization: "React.js"
    },
    {
      institution: "G.K.D Matric Higher Secondary School",
      degree: "Higher Secondary Education",
      duration: "2020 - 2022",
      location: "Tamil Nadu, India"
    }
  ];

  // Achievements
  const achievements = [
    "Launched multiple production-ready apps on no-cost hosting",
    "Built admin workflows for policy/coverage approvals",
    "Synchronized dynamic job data via Google Sheets API",
    "Engineered real-time chat with seen indicators and notifications",
    "Enhanced UX with responsive design, PWA install prompts, and secure data handling"
  ];

  useEffect(() => {
    const handleScroll = () => {
      const sections = navItems.map(item => item.id);
      const current = sections.find(section => {
        const element = document.getElementById(section);
        if (element) {
          const rect = element.getBoundingClientRect();
          return rect.top <= 100 && rect.bottom >= 100;
        }
        return false;
      });
      if (current) {
        setActiveSection(current);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
    setIsMenuOpen(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 relative overflow-x-hidden">
      {/* Animated Background Elements */}
      <div className="fixed inset-0 z-0">
        <div className="absolute top-20 left-10 w-72 h-72 bg-blue-500/10 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute bottom-20 right-10 w-96 h-96 bg-purple-500/10 rounded-full blur-3xl animate-pulse" style={{animationDelay: '2s'}}></div>
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-64 h-64 bg-pink-500/10 rounded-full blur-3xl animate-pulse" style={{animationDelay: '4s'}}></div>
      </div>

      {/* Navigation */}
      <nav className="fixed top-0 w-full bg-black/20 backdrop-blur-xl border-b border-white/10 z-50 transition-all duration-300">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="text-2xl font-bold bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
              MR
            </div>
            
            {/* Desktop Navigation */}
            <div className="hidden md:flex space-x-2">
              {navItems.map((item) => {
                const Icon = item.icon;
                return (
                  <button
                    key={item.id}
                    onClick={() => scrollToSection(item.id)}
                    className={`relative flex items-center space-x-2 px-4 py-2 rounded-xl transition-all duration-300 group ${
                      activeSection === item.id
                        ? 'bg-gradient-to-r from-blue-500/20 to-purple-500/20 text-blue-400 shadow-lg shadow-blue-500/20'
                        : 'text-gray-300 hover:text-white hover:bg-white/10'
                    }`}
                  >
                    <Icon size={18} />
                    <span className="font-medium">{item.label}</span>
                    {activeSection === item.id && (
                      <div className="absolute inset-0 bg-gradient-to-r from-blue-500/10 to-purple-500/10 rounded-xl blur-sm"></div>
                    )}
                  </button>
                );
              })}
            </div>

            {/* Mobile menu button */}
            <button
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="md:hidden text-white p-2 rounded-lg hover:bg-white/10 transition-colors"
            >
              {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>
        </div>

        {/* Mobile Navigation */}
        {isMenuOpen && (
          <div className="md:hidden bg-black/40 backdrop-blur-lg border-t border-white/10">
            <div className="px-4 pt-2 pb-3 space-y-1">
              {navItems.map((item) => {
                const Icon = item.icon;
                return (
                  <button
                    key={item.id}
                    onClick={() => scrollToSection(item.id)}
                    className={`flex items-center space-x-3 w-full px-3 py-3 rounded-lg transition-all duration-300 ${
                      activeSection === item.id
                        ? 'bg-gradient-to-r from-blue-500/20 to-purple-500/20 text-blue-400'
                        : 'text-gray-300 hover:text-white hover:bg-white/10'
                    }`}
                  >
                    <Icon size={20} />
                    <span className="font-medium">{item.label}</span>
                  </button>
                );
              })}
            </div>
          </div>
        )}
      </nav>

      {/* Hero Section */}
      <section className="relative pt-20 pb-20 px-4 min-h-screen flex items-center justify-center">
        <div className="max-w-7xl mx-auto text-center z-10">
          <div className="relative inline-block mb-8">
            <div className="relative">
              <div className="w-40 h-40 mx-auto rounded-full bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500 p-1 animate-spin" style={{animationDuration: '3s'}}>
                <div className="w-full h-full rounded-full bg-slate-900 flex items-center justify-center text-4xl font-bold text-white">
                  {personalInfo.name.split(' ').map(n => n[0]).join('')}
                </div>
              </div>
              <div className="absolute -bottom-3 -right-3 bg-green-500 w-10 h-10 rounded-full border-4 border-slate-900 flex items-center justify-center">
                <div className="w-3 h-3 bg-white rounded-full animate-pulse"></div>
              </div>
            </div>
          </div>
          
          <h1 className="text-5xl md:text-7xl font-bold mb-6 leading-tight">
            <span className="bg-gradient-to-r from-blue-400 via-purple-400 to-pink-400 bg-clip-text text-transparent animate-pulse">
              {personalInfo.name.split(' ')[0]}
            </span>
            <br />
            <span className="text-white">
              {personalInfo.name.split(' ').slice(1).join(' ')}
            </span>
          </h1>
          
          <p className="text-xl md:text-2xl text-gray-300 mb-8 max-w-4xl mx-auto leading-relaxed">
            {personalInfo.subtitle}
          </p>
          
          <div className="flex flex-wrap justify-center gap-4 mb-12">
            <a
              href={personalInfo.linkedin}
              target="_blank"
              rel="noopener noreferrer"
              className="group flex items-center space-x-3 bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 text-white px-8 py-4 rounded-full transition-all duration-300 transform hover:scale-105 hover:shadow-xl hover:shadow-blue-500/25"
            >
              <Linkedin size={20} />
              <span className="font-medium">LinkedIn</span>
            </a>
            <a
              href={personalInfo.github}
              target="_blank"
              rel="noopener noreferrer"
              className="group flex items-center space-x-3 bg-gradient-to-r from-gray-700 to-gray-800 hover:from-gray-800 hover:to-gray-900 text-white px-8 py-4 rounded-full transition-all duration-300 transform hover:scale-105 hover:shadow-xl hover:shadow-gray-500/25"
            >
              <Github size={20} />
              <span className="font-medium">GitHub</span>
            </a>
            <a
              href={`mailto:${personalInfo.email}`}
              className="group flex items-center space-x-3 bg-gradient-to-r from-purple-600 to-purple-700 hover:from-purple-700 hover:to-purple-800 text-white px-8 py-4 rounded-full transition-all duration-300 transform hover:scale-105 hover:shadow-xl hover:shadow-purple-500/25"
            >
              <Mail size={20} />
              <span className="font-medium">Contact</span>
            </a>
          </div>
          
          <button
            onClick={() => scrollToSection('about')}
            className="animate-bounce hover:animate-none transition-all duration-300 hover:scale-110"
          >
            <ChevronDown size={40} className="text-gray-400 hover:text-white" />
          </button>
        </div>
      </section>

      {/* About Section */}
      <section id="about" className="relative py-20 px-4">
        <div className="max-w-7xl mx-auto">
          <h2 className="text-4xl md:text-5xl font-bold text-center mb-16 bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
            About Me
          </h2>
          
          <div className="grid md:grid-cols-2 gap-12 items-center">
            <div className="space-y-6">
              <p className="text-lg text-gray-300 leading-relaxed">
                {personalInfo.description}
              </p>
              
              <div className="flex items-center text-gray-300 bg-white/5 backdrop-blur-sm rounded-lg p-4 border border-white/10">
                <MapPin size={24} className="mr-3 text-blue-400 flex-shrink-0" />
                <span className="font-medium">{personalInfo.location}</span>
              </div>
              
              <div className="grid grid-cols-1 gap-3 mt-8">
                <h3 className="text-xl font-semibold text-white mb-4">Key Achievements</h3>
                {achievements.map((achievement, index) => (
                  <div key={index} className="flex items-start space-x-3 bg-white/5 backdrop-blur-sm rounded-lg p-4 border border-white/10 hover:border-blue-500/30 transition-all duration-300">
                    <Star size={16} className="text-yellow-400 mt-1 flex-shrink-0" />
                    <span className="text-gray-300">{achievement}</span>
                  </div>
                ))}
              </div>
            </div>
            
            <div className="space-y-8">
              <h3 className="text-2xl font-semibold text-white mb-6">Tech Stack</h3>
              <div className="space-y-6">
                {skills.map((skillGroup, index) => (
                  <div key={index} className="bg-white/5 backdrop-blur-sm rounded-xl p-6 border border-white/10 hover:border-purple-500/30 transition-all duration-300">
                    <h4 className="text-lg font-medium mb-4 text-blue-400">{skillGroup.category}</h4>
                    <div className="flex flex-wrap gap-2">
                      {skillGroup.items.map((skill, skillIndex) => (
                        <span
                          key={skillIndex}
                          className="bg-gradient-to-r from-blue-500/20 to-purple-500/20 text-gray-300 px-4 py-2 rounded-full text-sm font-medium hover:from-blue-500/30 hover:to-purple-500/30 hover:scale-105 transition-all duration-300 cursor-pointer border border-blue-500/20"
                        >
                          {skill}
                        </span>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Experience Section */}
      <section id="experience" className="relative py-20 px-4 bg-black/20">
        <div className="max-w-7xl mx-auto">
          <h2 className="text-4xl md:text-5xl font-bold text-center mb-16 bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
            Experience
          </h2>
          
          <div className="space-y-8">
            {experience.map((exp, index) => (
              <div key={index} className="bg-white/5 backdrop-blur-lg rounded-2xl p-8 border border-white/10 hover:border-blue-500/50 transition-all duration-300 hover:scale-105 hover:shadow-2xl hover:shadow-blue-500/10">
                <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-6">
                  <div>
                    <h3 className="text-2xl font-semibold text-white mb-2">{exp.position}</h3>
                    <p className="text-blue-400 text-xl font-medium">{exp.company}</p>
                  </div>
                  <div className="text-right mt-4 md:mt-0">
                    <p className="text-gray-300 font-medium">{exp.duration}</p>
                    <p className="text-gray-400">{exp.location} â€¢ {exp.type}</p>
                  </div>
                </div>
                
                <p className="text-gray-300 mb-6 text-lg">{exp.description}</p>
                
                <ul className="space-y-3">
                  {exp.achievements.map((achievement, achievementIndex) => (
                    <li key={achievementIndex} className="flex items-start space-x-3">
                      <div className="w-2 h-2 bg-gradient-to-r from-blue-400 to-purple-400 rounded-full mt-3 flex-shrink-0"></div>
                      <span className="text-gray-300">{achievement}</span>
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Projects Section */}
      <section id="projects" className="relative py-20 px-4">
        <div className="max-w-7xl mx-auto">
          <h2 className="text-4xl md:text-5xl font-bold text-center mb-16 bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
            Featured Projects
          </h2>
          
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
            {projects.map((project, index) => (
              <div key={index} className="group bg-white/5 backdrop-blur-lg rounded-2xl overflow-hidden border border-white/10 hover:border-blue-500/50 transition-all duration-500 transform hover:scale-105 hover:shadow-2xl hover:shadow-blue-500/20">
                <div className="p-6 h-full flex flex-direction flex-col">
                  <div className="flex items-center justify-between mb-4">
                    <h3 className="text-xl font-semibold text-white group-hover:text-blue-400 transition-colors duration-300">{project.title}</h3>
                    <span className="bg-gradient-to-r from-green-500/20 to-emerald-500/20 text-green-400 px-3 py-1 rounded-full text-xs font-medium border border-green-500/30">
                      {project.status}
                    </span>
                  </div>
                  
                  <p className="text-gray-300 mb-4 flex-grow">{project.description}</p>
                  
                  <ul className="space-y-2 mb-6">
                    {project.features.map((feature, featureIndex) => (
                      <li key={featureIndex} className="flex items-start space-x-2">
                        <div className="w-1.5 h-1.5 bg-blue-400 rounded-full mt-2 flex-shrink-0"></div>
                        <span className="text-gray-400 text-sm">{feature}</span>
                      </li>
                    ))}
                  </ul>
                  
                  <div className="flex flex-wrap gap-2 mb-6">
                    {project.tech.map((tech, techIndex) => (
                      <span
                        key={techIndex}
                        className="bg-blue-500/20 text-blue-300 px-3 py-1 rounded-full text-xs font-medium border border-blue-500/30"
                      >
                        {tech}
                      </span>
                    ))}
                  </div>
                  
                  <div className="flex space-x-3 mt-auto">
                    <a
                      href={project.liveUrl}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex items-center space-x-2 bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 text-white px-4 py-2 rounded-lg transition-all duration-300 text-sm font-medium hover:shadow-lg hover:shadow-blue-500/25 flex-1 justify-center"
                    >
                      <ExternalLink size={16} />
                      <span>Live Demo</span>
                    </a>
                    <a
                      href={project.githubUrl}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex items-center space-x-2 bg-gradient-to-r from-gray-700 to-gray-800 hover:from-gray-800 hover:to-gray-900 text-white px-4 py-2 rounded-lg transition-all duration-300 text-sm font-medium hover:shadow-lg hover:shadow-gray-500/25 flex-1 justify-center"
                    >
                      <Github size={16} />
                      <span>Code</span>
                    </a>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Education Section */}
      <section id="education" className="relative py-20 px-4 bg-black/20">
        <div className="max-w-7xl mx-auto">
          <h2 className="text-4xl md:text-5xl font-bold text-center mb-16 bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
            Education
          </h2>
          
          <div className="space-y-8">
            {education.map((edu, index) => (
              <div key={index} className="bg-white/5 backdrop-blur-lg rounded-2xl p-8 border border-white/10 hover:border-purple-500/50 transition-all duration-300 hover:scale-105 hover:shadow-2xl hover:shadow-purple-500/10">
                <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-4">
                  <div>
                    <h3 className="text-2xl font-semibold text-white mb-2">{edu.degree}</h3>
                    <p className="text-purple-400 text-xl font-medium">{edu.institution}</p>
                    {edu.specialization && (
                      <p className="text-gray-400 mt-1">Specialization: <span className="text-blue-400 font-medium">{edu.specialization}</span></p>
                    )}
                  </div>
                  <div className="text-right mt-4 md:mt-0">
                    <p className="text-gray-300 font-medium">{edu.duration}</p>
                    <p className="text-gray-400">{edu.location}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="relative py-12 px-4 border-t border-white/10 bg-black/30">
        <div className="max-w-7xl mx-auto text-center">
          <p className="text-gray-400 mb-6 text-lg">
            Â© 2025 {personalInfo.name}. Built with React & Tailwind CSS.
          </p>
          <div className="flex justify-center space-x-6">
            <a
              href={personalInfo.linkedin}
              target="_blank"
              rel="noopener noreferrer"
              className="text-gray-400 hover:text-blue-400 transition-all duration-300 transform hover:scale-110"
            >
              <Linkedin size={28} />
            </a>
            <a
              href={personalInfo.github}
              target="_blank"
              rel="noopener noreferrer"
              className="text-gray-400 hover:text-white transition-all duration-300 transform hover:scale-110"
            >
              <Github size={28} />
            </a>
            <a
              href={`mailto:${personalInfo.email}`}
              className="text-gray-400 hover:text-purple-400 transition-all duration-300 transform hover:scale-110"
            >
              <Mail size={28} />
            </a>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default Portfolio;
