import React, { useState, useEffect } from 'react';
import { Github, Linkedin, Mail, MapPin, ExternalLink, ChevronDown, Menu, X, Code, Briefcase, GraduationCap, User, Star } from 'lucide-react';

// Navigation items
const navItems = [
  { id: 'about', label: 'About', icon: User },
  { id: 'experience', label: 'Experience', icon: Briefcase },
  { id: 'projects', label: 'Projects', icon: Code },
  { id: 'education', label: 'Education', icon: GraduationCap }
];

const Portfolio = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [activeSection, setActiveSection] = useState('about');

  // Personal Information
  const personalInfo = {
    name: "Mohammad Razak Abdul Rasheeth",
    title: "Full-Stack Developer",
    subtitle: "Software Engineer | Skilled in JavaScript, React, Node.js, Java, Express & MongoDB",
    description: "ðŸ‘‹ Hi, I'm Mohammad Razak, a full-stack developer passionate about building impactful, user-first web and mobile solutions. I specialize in MERN and React/Node.js with specialty in authentication, real-time systems, and deployment on zero-cost platforms.",
    location: "Coimbatore, Tamil Nadu, India",
    email: "your.email@example.com",
    phone: "+91 XXXXXXXXXX",
    linkedin: "https://linkedin.com/in/mohammadrazak-abdulrasheeth",
    github: "https://github.com/MohammadRazaak"
  };

  // Skills
  const skills = [
    {
      category: "Frontend",
      items: ["React", "Redux", "JavaScript", "CSS3", "Material UI", "React Router", "HTML5", "Responsive Design"]
    },
    {
      category: "Backend",
      items: ["Node.js", "Express", "Socket.io", "RESTful APIs", "JWT Authentication", "Java"]
    },
    {
      category: "Database",
      items: ["MongoDB", "PostgreSQL", "Prisma ORM", "Firebase"]
    },
    {
      category: "Tools & Deployment",
      items: ["Git", "Netlify", "Vercel", "Heroku", "Railway", "Google APIs", "Microsoft Azure"]
    }
  ];

  // Projects
  const projects = [
    {
      title: "Raz Insurance",
      description: "A sleek insurance platform enabling users to buy, manage, and verify policies and coverage online.",
      features: [
        "Full-stack with React, Redux, Node, Express & PostgreSQL via Prisma ORM",
        "Customer/admin roles with JWT-based auth & admin approval flows",
        "Responsive design and SEO-friendly PWA hosted on Netlify and Railway/Heroku"
      ],
      tech: ["React", "Redux", "Node.js", "Express", "PostgreSQL", "Prisma ORM"],
      liveUrl: "#",
      githubUrl: "#",
      status: "Production Ready"
    },
    {
      title: "RazChatz",
      description: "A real-time chat application built with React and Socket.io.",
      features: [
        "Instant messaging, seen/delivered statuses, emoji picker",
        "JWT authentication, Web Notifications, service-worker optimized for PWA",
        "Hosted as a PWA for installable mobile experience"
      ],
      tech: ["React", "Socket.io", "Node.js", "MongoDB", "Material UI"],
      liveUrl: "https://razchatz.netlify.app/",
      githubUrl: "#",
      status: "Live"
    },
    {
      title: "Placement Training System (PTS)",
      description: "An end-to-end placement prep suite for students and TPOs.",
      features: [
        "MERN stack with MongoDB and Express",
        "Integrated Google Sheets API for live job data management",
        "Firebase Storage for image hosting",
        "JWT authentication, role-based dashboards, quizzes, video lessons"
      ],
      tech: ["React", "Node.js", "MongoDB", "Google Sheets API", "Firebase"],
      liveUrl: "https://pts-by-razak.netlify.app",
      githubUrl: "#",
      status: "Live"
    }
  ];

  // Experience
  const experience = [
    {
      company: "ValueMomentum",
      position: "Software Engineer Intern",
      duration: "Feb 2025 - Present",
      location: "Coimbatore, Tamil Nadu, India",
      type: "On-site",
      description: "Working with Microsoft Azure and developing scalable software solutions.",
      achievements: [
        "Developing enterprise-level applications",
        "Working with cloud technologies",
        "Collaborating with cross-functional teams"
      ]
    }
  ];

  // Education
  const education = [
    {
      institution: "KG College of Arts and Science",
      degree: "Bachelor of Computer Application",
      duration: "Jul 2022 - May 2025",
      location: "Coimbatore, Tamil Nadu, India",
      specialization: "React.js"
    },
    {
      institution: "G.K.D Matric Higher Secondary School",
      degree: "Higher Secondary Education",
      duration: "2020 - 2022",
      location: "Tamil Nadu, India"
    }
  ];

  // Achievements
  const achievements = [
    "Launched multiple production-ready apps on no-cost hosting",
    "Built admin workflows for policy/coverage approvals",
    "Synchronized dynamic job data via Google Sheets API",
    "Engineered real-time chat with seen indicators and notifications",
    "Enhanced UX with responsive design, PWA install prompts, and secure data handling"
  ];

  useEffect(() => {
    const handleScroll = () => {
      const sections = navItems.map(item => item.id);
      const current = sections.find(section => {
        const element = document.getElementById(section);
        if (element) {
          const rect = element.getBoundingClientRect();
          return rect.top <= 100 && rect.bottom >= 100;
        }
        return false;
      });
      if (current) {
        setActiveSection(current);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
    setIsMenuOpen(false);
  };

  return (
    <div style={styles.container}>
      {/* Animated Background Elements */}
      <div style={styles.backgroundElements}>
        <div style={{...styles.bgCircle1, ...styles.animatedCircle}}></div>
        <div style={{...styles.bgCircle2, ...styles.animatedCircle, animationDelay: '2s'}}></div>
        <div style={{...styles.bgCircle3, ...styles.animatedCircle, animationDelay: '4s'}}></div>
      </div>

      {/* Navigation */}
      <nav style={styles.nav}>
        <div style={styles.navContainer}>
          <div style={styles.navContent}>
            <div style={styles.logo}>MR</div>
            
            {/* Desktop Navigation */}
            <div style={styles.desktopNav}>
              {navItems.map((item) => {
                const Icon = item.icon;
                return (
                  <button
                    key={item.id}
                    onClick={() => scrollToSection(item.id)}
                    style={{
                      ...styles.navButton,
                      ...(activeSection === item.id ? styles.navButtonActive : {})
                    }}
                  >
                    <Icon size={18} />
                    <span>{item.label}</span>
                  </button>
                );
              })}
            </div>

            {/* Mobile menu button */}
            <button
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              style={styles.mobileMenuButton}
            >
              {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>
        </div>

        {/* Mobile Navigation */}
        {isMenuOpen && (
          <div style={styles.mobileNav}>
            <div style={styles.mobileNavContent}>
              {navItems.map((item) => {
                const Icon = item.icon;
                return (
                  <button
                    key={item.id}
                    onClick={() => scrollToSection(item.id)}
                    style={{
                      ...styles.mobileNavButton,
                      ...(activeSection === item.id ? styles.mobileNavButtonActive : {})
                    }}
                  >
                    <Icon size={20} />
                    <span>{item.label}</span>
                  </button>
                );
              })}
            </div>
          </div>
        )}
      </nav>

      {/* Hero Section */}
      <section style={styles.heroSection}>
        <div style={styles.heroContainer}>
          <div style={styles.profileImageContainer}>
            <div style={styles.profileImageOuter}>
              <div style={styles.profileImageInner}>
                {personalInfo.name.split(' ').map(n => n[0]).join('')}
              </div>
              <div style={styles.statusIndicator}>
                <div style={styles.statusDot}></div>
              </div>
            </div>
          </div>
          
          <h1 style={styles.heroTitle}>
            <span style={styles.firstName}>
              {personalInfo.name.split(' ')[0]}
            </span>
            <br />
            <span style={styles.lastName}>
              {personalInfo.name.split(' ').slice(1).join(' ')}
            </span>
          </h1>
          
          <p style={styles.heroSubtitle}>
            {personalInfo.subtitle}
          </p>
          
          <div style={styles.heroButtons}>
            <a
              href={personalInfo.linkedin}
              target="_blank"
              rel="noopener noreferrer"
              style={{...styles.heroButton, ...styles.linkedinButton}}
            >
              <Linkedin size={20} />
              <span>LinkedIn</span>
            </a>
            <a
              href={personalInfo.github}
              target="_blank"
              rel="noopener noreferrer"
              style={{...styles.heroButton, ...styles.githubButton}}
            >
              <Github size={20} />
              <span>GitHub</span>
            </a>
            <a
              href={`mailto:${personalInfo.email}`}
              style={{...styles.heroButton, ...styles.emailButton}}
            >
              <Mail size={20} />
              <span>Contact</span>
            </a>
          </div>
          
          <button
            onClick={() => scrollToSection('about')}
            style={styles.scrollButton}
          >
            <ChevronDown size={40} />
          </button>
        </div>
      </section>

      {/* About Section */}
      <section id="about" style={styles.section}>
        <div style={styles.sectionContainer}>
          <h2 style={styles.sectionTitle}>About Me</h2>
          
          <div style={styles.aboutGrid}>
            <div style={styles.aboutLeft}>
              <p style={styles.aboutDescription}>
                {personalInfo.description}
              </p>
              
              <div style={styles.locationCard}>
                <MapPin size={24} style={styles.locationIcon} />
                <span>{personalInfo.location}</span>
              </div>
              
              <div style={styles.achievementsSection}>
                <h3 style={styles.achievementsTitle}>Key Achievements</h3>
                {achievements.map((achievement, index) => (
                  <div key={index} style={styles.achievementCard}>
                    <Star size={16} style={styles.achievementIcon} />
                    <span style={styles.achievementText}>{achievement}</span>
                  </div>
                ))}
              </div>
            </div>
            
            <div style={styles.aboutRight}>
              <h3 style={styles.skillsTitle}>Tech Stack</h3>
              <div style={styles.skillsContainer}>
                {skills.map((skillGroup, index) => (
                  <div key={index} style={styles.skillGroup}>
                    <h4 style={styles.skillGroupTitle}>{skillGroup.category}</h4>
                    <div style={styles.skillTags}>
                      {skillGroup.items.map((skill, skillIndex) => (
                        <span key={skillIndex} style={styles.skillTag}>
                          {skill}
                        </span>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Experience Section */}
      <section id="experience" style={styles.experienceSection}>
        <div style={styles.sectionContainer}>
          <h2 style={styles.sectionTitle}>Experience</h2>
          
          <div style={styles.experienceContainer}>
            {experience.map((exp, index) => (
              <div key={index} style={styles.experienceCard}>
                <div style={styles.experienceHeader}>
                  <div>
                    <h3 style={styles.experiencePosition}>{exp.position}</h3>
                    <p style={styles.experienceCompany}>{exp.company}</p>
                  </div>
                  <div style={styles.experienceDetails}>
                    <p style={styles.experienceDuration}>{exp.duration}</p>
                    <p style={styles.experienceLocation}>{exp.location} â€¢ {exp.type}</p>
                  </div>
                </div>
                
                <p style={styles.experienceDescription}>{exp.description}</p>
                
                <ul style={styles.achievementsList}>
                  {exp.achievements.map((achievement, achievementIndex) => (
                    <li key={achievementIndex} style={styles.achievementItem}>
                      <div style={styles.achievementBullet}></div>
                      <span>{achievement}</span>
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Projects Section */}
      <section id="projects" style={styles.section}>
        <div style={styles.sectionContainer}>
          <h2 style={styles.sectionTitle}>Featured Projects</h2>
          
          <div style={styles.projectsGrid}>
            {projects.map((project, index) => (
              <div key={index} style={styles.projectCard}>
                <div style={styles.projectContent}>
                  <div style={styles.projectHeader}>
                    <h3 style={styles.projectTitle}>{project.title}</h3>
                    <span style={styles.projectStatus}>
                      {project.status}
                    </span>
                  </div>
                  
                  <p style={styles.projectDescription}>{project.description}</p>
                  
                  <ul style={styles.projectFeatures}>
                    {project.features.map((feature, featureIndex) => (
                      <li key={featureIndex} style={styles.projectFeature}>
                        <div style={styles.featureBullet}></div>
                        <span>{feature}</span>
                      </li>
                    ))}
                  </ul>
                  
                  <div style={styles.projectTech}>
                    {project.tech.map((tech, techIndex) => (
                      <span key={techIndex} style={styles.techTag}>
                        {tech}
                      </span>
                    ))}
                  </div>
                  
                  <div style={styles.projectButtons}>
                    <a
                      href={project.liveUrl}
                      target="_blank"
                      rel="noopener noreferrer"
                      style={{...styles.projectButton, ...styles.liveButton}}
                    >
                      <ExternalLink size={16} />
                      <span>Live Demo</span>
                    </a>
                    <a
                      href={project.githubUrl}
                      target="_blank"
                      rel="noopener noreferrer"
                      style={{...styles.projectButton, ...styles.codeButton}}
                    >
                      <Github size={16} />
                      <span>Code</span>
                    </a>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Education Section */}
      <section id="education" style={styles.educationSection}>
        <div style={styles.sectionContainer}>
          <h2 style={styles.sectionTitle}>Education</h2>
          
          <div style={styles.educationContainer}>
            {education.map((edu, index) => (
              <div key={index} style={styles.educationCard}>
                <div style={styles.educationHeader}>
                  <div>
                    <h3 style={styles.educationDegree}>{edu.degree}</h3>
                    <p style={styles.educationInstitution}>{edu.institution}</p>
                    {edu.specialization && (
                      <p style={styles.educationSpecialization}>
                        Specialization: <span style={styles.specializationText}>{edu.specialization}</span>
                      </p>
                    )}
                  </div>
                  <div style={styles.educationDetails}>
                    <p style={styles.educationDuration}>{edu.duration}</p>
                    <p style={styles.educationLocation}>{edu.location}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer style={styles.footer}>
        <div style={styles.footerContainer}>
          <p style={styles.footerText}>
            Â© 2025 {personalInfo.name}. Built with React & Pure CSS.
          </p>
          <div style={styles.footerSocial}>
            <a
              href={personalInfo.linkedin}
              target="_blank"
              rel="noopener noreferrer"
              style={styles.socialLink}
            >
              <Linkedin size={28} />
            </a>
            <a
              href={personalInfo.github}
              target="_blank"
              rel="noopener noreferrer"
              style={styles.socialLink}
            >
              <Github size={28} />
            </a>
            <a
              href={`mailto:${personalInfo.email}`}
              style={styles.socialLink}
            >
              <Mail size={28} />
            </a>
          </div>
        </div>
      </footer>

      <style>{`
        @keyframes pulse {
          0%, 100% { opacity: 0.4; }
          50% { opacity: 0.8; }
        }
        
        @keyframes spin {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }
        
        @keyframes bounce {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-10px); }
        }
        
        @media (max-width: 768px) {
          .desktop-nav { display: none !important; }
          .mobile-menu-button { display: block !important; }
          .hero-title { font-size: 3rem !important; }
          .hero-subtitle { font-size: 1.25rem !important; }
          .about-grid { grid-template-columns: 1fr !important; }
          .projects-grid { grid-template-columns: 1fr !important; }
          .experience-header { flex-direction: column !important; }
          .education-header { flex-direction: column !important; }
          .hero-buttons { flex-direction: column !important; }
        }
        
        @media (max-width: 640px) {
          .projects-grid { grid-template-columns: 1fr !important; }
          .hero-buttons { flex-direction: column !important; gap: 1rem !important; }
        }
      `}</style>
    </div>
  );
};

    export default Portfolio;
